type Queue struct{
	data	[26]note
	head	int
	tail	int
}

func (q *Queue) Put(x int, y int, s int) {
	q.data[q.tail].x = x
	q.data[q.tail].y = y
	q.data[q.tail].step = s
	q.tail += 1
}

func (q *Queue) Get() (int,int,int) {
	var x,y,s int
	x = q.data[q.head].x
	y = q.data[q.head].y
	s = q.data[q.head].step
	q.head++
	return x,y,s
}

func (q *Queue) IsEmpty() bool{
	if q.head ==  q.tail {
		return true
	}else if q.head > q.tail {
		return false
	}else if q.head < q.tail {
		return false
	}
	return false
}

